'use stirct';

var config = {};

/**
 *	Allow the user to config the app
 */
module.exports = function(key, val) {
	if (!config[key]) return;
	return config[key].call(this, val);
};

/**
 *	Set a view engine
 *	@param {Function} viewEngine
 */
config.view_engine = function(viewEngine) {
	this.viewEngine = viewEngine;
	return false;
};

/**
 *	Set a base view directory
 *	@param {String} path
 */
config.views = function(path) {
	this.viewsPath = path;
	return false;
};
